import React, {useState} from 'react';

const Font = ({alternate}) => {
    const [fontSize, setFontSize] = useState(16);
    return (
        <>
        <div className="fontButtons">
        <button
          className="fontButton"
        //   onClick={() => setFontSize(fontSize + 2)}
          onClick={() => setFontSize(fontSize + 2)}
        >
          bigger +
        </button>
        <button
          className="fontButton"
        //   onClick={() => setFontSize(fontSize - 2)}
          onClick={() => setFontSize(alternate- 2)}
        >
          smaller -
        </button>
        </div>
      </>
    )
}

export default Font
